import 'package:beauty_textfield/beauty_textfield.dart';import 'package:circular_clip_route/circular_clip_route.dart';import 'package:flip_card/flip_card.dart';import 'package:flutter/material.dart';import 'package:flutter_custom_clippers/flutter_custom_clippers.dart';import 'package:flutter_login_ui/homepage.dart';import 'package:google_fonts/google_fonts.dart';class LoginScreen extends StatelessWidget {  final _key1 = GlobalKey();  @override  Widget build(BuildContext context) {    return Scaffold(      body: SingleChildScrollView(        child: Container(          height: MediaQuery.of(context).size.height,          child: Column(            children: [              ClipPath(                clipper: WaveClipperTwo(),                child: Image.asset('assets/login.jpg',                fit: BoxFit.cover,                height: MediaQuery.of(context).size.height / 2.5,),              ),              Text('WINTER',style: GoogleFonts.aclonica(                fontSize: 30,                color: Colors.blue,                fontWeight: FontWeight.w700,                letterSpacing: 10,              ),),              SizedBox(height: 10,),              Padding(padding: EdgeInsets.symmetric(horizontal: 20,vertical: 5),              child: BeautyTextfield(                  width: double.maxFinite,                  height: 45,                  backgroundColor: Colors.blue.shade50,                  cornerRadius: BorderRadius.circular(15),                  prefixIcon: Icon(Icons.person),                  inputType: TextInputType.text,                duration: Duration(milliseconds: 200),                placeholder: 'USERNAME',                maxLines: 1,              ),              ),              Padding(padding: EdgeInsets.symmetric(horizontal: 20,vertical: 5),                child: BeautyTextfield(                  width: double.maxFinite,                  height: 45,                  duration: Duration(milliseconds: 200),                  suffixIcon: Icon(Icons.remove_red_eye_rounded),                  obscureText: true,                  backgroundColor: Colors.blue.shade50,                  cornerRadius: BorderRadius.circular(15),                  prefixIcon: Icon(Icons.lock_rounded),                  inputType: TextInputType.text,                  placeholder: 'PASSWORD',                  maxLines: 1,                ),              ),              SizedBox(                height: 30,              ),              FlipCard(                direction: FlipDirection.VERTICAL,                onFlipDone: (isDone){                  Navigator.push(context, CircularClipRoute(                      expandFrom: _key1.currentContext,                      curve: Curves.linearToEaseOut,                      transitionDuration: Duration(seconds: 2),                      reverseCurve: Curves.slowMiddle,                      builder: (_)=> HomePage()                  ));                },                  front: Container(                    key: _key1,                    height: 40,                    alignment: Alignment.center,                    margin: EdgeInsets.symmetric(horizontal: 90),                    decoration: BoxDecoration(                      color: Colors.blue,                      borderRadius: BorderRadius.circular(20),                      border: Border.all(                        color: Colors.blue.shade900                      ),                    ),                    child: Text('LOGIN',style: GoogleFonts.alice(                      fontSize: 20,                      color: Colors.white                    ),),                  ),                  back: Container(                    height: 40,                    alignment: Alignment.center,                    margin: EdgeInsets.symmetric(horizontal: 90),                    decoration: BoxDecoration(                      color: Colors.green,                      borderRadius: BorderRadius.circular(20),                      border: Border.all(                          color: Colors.green.shade900                      ),                    ),                    child: Text('DONE',style: GoogleFonts.alice(                        fontSize: 20,                        color: Colors.white                    ),),                  ),              ),              Expanded(                  child: Align(                    alignment: Alignment.bottomCenter,                    child: Container(                      alignment: Alignment.center,                      color: Colors.blue,                      height: MediaQuery.of(context).size.height * 0.08,                      child: Text('DON\'T HAVE ACCOUNT? SIGN UP',                      style: GoogleFonts.alef(                        fontSize: 20,                        color: Colors.white,                      ),),                    ),                  )              )            ],          ),        ),      ),    );  }}